package common;

public class Calbasic extends Calculadora{
	public Calbasic() {
		
	}
	
	
	//como

	@Override
	public double sumar(int a, double b) {
		// TODO Auto-generated method stub
		return a+b;
	}

	@Override
	public double restar(int a, double b) {
		// TODO Auto-generated method stub
		return a-b;
	}

}
