package common;

public class calBasica extends Calculadora {
	
	public calBasica() {
		
	}
	
	
	
	@Override
	public double sumar(int a, double b) {
		// TODO Auto-generated method stub
		return a+b;
	}

	@Override
	public double restar(int a, double b) {
		// TODO Auto-generated method stub
		return a-b;
	}
	
	
}
