package common;

public class CalcBasica extends Calculadora{
	
	public CalcBasica() {
		
	}
	
	//como hacer eso

	@Override
	public double sumar(int a, double b) {
		// TODO Auto-generated method stub
		return a + b;
	}

	@Override
	public double restar(int a, double b) {
		// TODO Auto-generated method stub
		return a - b;
		
	}

}
